import React from 'react';
import Swal from 'sweetalert2';
import '../contents/css/updateBookForm/updateBookForm.css';

class UpdateBookForm extends React.Component {

    constructor(props) {
        super(props);
        this.onTitleChange = this.onTitleChange.bind(this);
        this.onAuthorChange = this.onAuthorChange.bind(this);
        this.onDescriptionChange = this.onDescriptionChange.bind(this);
        this.onPublishedChange = this.onPublishedChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            title: props.book ? props.book.title : '',
            author: props.book ? props.book.author : '',
            description: props.book ? props.book.description : '',
            published: props.book ? props.book.published : ''
        };
    }

    onTitleChange(e) {
        const title = e.target.value;
        this.setState(() => ({ title: title }));
    }

    onAuthorChange(e) {
        const author = e.target.value;
        this.setState(() => ({ author: author }));
    }

    onDescriptionChange(e) {
        const description = e.target.value;
        this.setState(() => ({ description: description }));
    }

    onPublishedChange(e) {
        const published = e.target.value;
        this.setState(() => ({ published: published }));
    }

    onSubmit(e) {
        e.preventDefault();

        if (!this.state.title || !this.state.author || !this.state.published) {
            Swal({
                type: 'error',
                title: '<i>Error!</i>',
                text: 'Please set title, author, description & published!',
                confirmButtonText: '<i class="fa fa-thumbs-down"></i> Sorry!',
                confirmButtonAriaLabel: 'Thumbs down',
                buttonsStyling: false,
                confirmButtonClass: 'btn btn-dark btn-lg'
              });
        } else {
            this.props.onSubmitBook(
                {
                    title: this.state.title,
                    author: this.state.author,
                    description: this.state.description,
                    published: this.state.published
                }
            );
        }
    }

    render() {

        const updatebookformheadjsx = <h1 id="setHead">Update Book</h1>;

        const updatebookformjsx = <div class="w3-main">
        <div class="about-bottom main-agile book-form">
            <form onSubmit={this.onSubmit}>
                <div class="form-date-w3-agileits">
                    <label class="animated fadeInLeftBig update1_animation"> Title </label>
                    <input type="text" class="animated fadeInLeftBig update1_animation" value={this.state.title} onChange={this.onTitleChange} placeholder="Enter Title"/>
                    <label class="animated fadeInRightBig update2_animation"> Description </label>
                    <input type="text" class="animated fadeInRightBig update2_animation" value={this.state.description} onChange={this.onDescriptionChange} placeholder="Enter Description"/>
                    <label class="animated fadeInLeftBig update1_animation"> Author </label>
                    <input type="text" class="animated fadeInLeftBig update1_animation" value={this.state.author} onChange={this.onAuthorChange} placeholder="Enter Author"/>
                    <label class="animated fadeInRightBig update2_animation"> Published </label>
                    <input type="text" class="animated fadeInRightBig update2_animation" value={this.state.published} onChange={this.onPublishedChange} placeholder="Enter Published"/>
                </div>
                <div class="make wow shake">
                    <input type="submit" class="animated fadeInDownBig update4_animation" value="Update Book"/>
                </div>
            </form>
        </div>
    </div>;

        return (
<div>
    <div class="text-center" id="jumb">
        {updatebookformheadjsx}
    </div>
    <div class="container-fluid">
        <div class="row" id="setAddBack">
            <div class="col-sm-12">
                {updatebookformjsx}
            </div>
        </div>
    </div>
</div>
        );
    }
}
export default UpdateBookForm;
