import React from 'react';
import '../contents/css/addBookForm/addBookForm.css';
import Swal from 'sweetalert2';

export class AddBookForm extends React.Component {

    constructor(props) {
        super(props);
        this.onTitleChange = this.onTitleChange.bind(this);
        this.onAuthorChange = this.onAuthorChange.bind(this);
        this.onDescriptionChange = this.onDescriptionChange.bind(this);
        this.onPublishedChange = this.onPublishedChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            title: props.book ? props.book.title : '',
            author: props.book ? props.book.author : '',
            description: props.book ? props.book.description : '',
            published: props.book ? props.book.published : 0
        };
    }

    onTitleChange(e) {
        const title = e.target.value;
        this.setState(() => ({ title: title }));
    }

    onAuthorChange(e) {
        const author = e.target.value;
        this.setState(() => ({ author: author }));
    }

    onDescriptionChange(e) {
        const description = e.target.value;
        this.setState(() => ({ description: description }));
    }

    onPublishedChange(e) {
        const published = parseInt(e.target.value);
        this.setState(() => ({ published: published }));
    }

    onSubmit(e) {
        e.preventDefault();

        if (!this.state.title || !this.state.author || !this.state.published) {
            Swal({
                type: 'error',
                title: '<i>Error!</i>',
                text: 'Please set title & author & published!',
                confirmButtonText: '<i class="fa fa-thumbs-down"></i> Sorry!',
                confirmButtonAriaLabel: 'Thumbs down',
                buttonsStyling: false,
                confirmButtonClass: 'btn btn-dark btn-lg'
              });
        } else {
            this.props.onSubmitBook(
                {
                    title: this.state.title,
                    author: this.state.author,
                    description: this.state.description,
                    published: this.state.published
                }
            );
        }
    }

    render() {
        return (
<div>
    <div class="text-center" id="jumb">
        <h1 id="setHead">Add Book</h1>
    </div>
    <div class="container-fluid">
        <div class="row" id="setAddBack">
            <div class="col-sm-12">
                <div class="main-w3layouts-agileinfo">
                    <div class="wthree-form">

                        <form onSubmit={this.onSubmit}>

								<div class="form-sub-w3 animated fadeInLeftBig add1_animation">
									<input type="text" value={this.state.title} onChange={this.onTitleChange} placeholder="TITLE"/>
								<div class="icon-w3">
                                    <i class="fas fa-ad"></i>
								</div>
								</div>

                                <div class="form-sub-w3 animated fadeInRightBig add2_animation">
									<input type="text" value={this.state.description} onChange={this.onDescriptionChange} placeholder="DESCRIPTION"/>
								<div class="icon-w3">
                                    <i class="fas fa-mail-bulk"></i>
								</div>
								</div>

                                <div class="form-sub-w3 animated fadeInLeftBig add1_animation">
									<input type="text" value={this.state.author} onChange={this.onAuthorChange} placeholder="AUTHOR"/>
								<div class="icon-w3">
                                    <i class="fas fa-pen-fancy"></i>
								</div>
								</div>

								<div class="form-sub-w3 animated fadeInRightBig add2_animation">
									<input type="text" value={this.state.published} onChange={this.onPublishedChange} placeholder="PUBLISHED"/>
								<div class="icon-w3">
                                    <i class="fas fa-clipboard"></i>
								</div>
								</div>

								<div class="clear"></div>
								<div class="submit-agileits">
									<input type="submit" class="animated fadeInDownBig add4_animation" value="Add Book"/>
								</div>

						</form>

                    </div>
                </div>
            </div>
        </div>
    </div>

</div>
        );
    }
}
